let dec_btn=document.querySelectorAll(".decrement-btn"),inc_btn=document.querySelectorAll(".increment-btn"),all_btn=[...inc_btn,...dec_btn];dec_btn.forEach(n=>n.addEventListener("click",function(){var i=n.id.split("-")[2],t=document.querySelector("#button-value-"+i);t.innerHTML<=1?t.innerHTML=1:t.innerHTML--}));inc_btn.forEach(n=>n.addEventListener("click",function(){var t=n.id.split("-")[2],i=document.querySelector("#button-value-"+t);i.innerHTML++}));all_btn.forEach(n=>n.addEventListener("click",function(){var i=n.id.split("-")[2],t=document.querySelector("#button-value-"+i);t.innerHTML<=1?dec_btn.forEach(n=>(()=>{n.classList.add("isInactive")})()):t.innerHTML>=1&&dec_btn.forEach(n=>(()=>{n.classList.remove("isInactive")})())}));