let dec_btn=document.querySelectorAll(".decrement-btn"),inc_btn=document.querySelectorAll(".increment-btn");dec_btn.forEach(n=>n.addEventListener("click",function(){var r=n.id.split("-")[2],i=document.querySelector("#button-value-"+r),t=i.innerHTML;t<=0?(t=0,n.classList.add("isInactive")):(n.classList.remove("isInactive"),t--);i.innerHTML=t}));inc_btn.forEach(n=>n.addEventListener("click",function(){var t=n.id.split("-")[2],i=document.querySelector("#button-value-"+t);i.innerHTML++}));