function decCheck(){value<=0?(value=0,dec_btn.classList.add("isInactive")):(dec_btn.classList.remove("isInactive"),value--)}let dec_btn=document.querySelector(".decrement-btn"),inc_btn=document.querySelector(".increment-btn"),label=document.querySelector(".input-value"),value=1;dec_btn.addEventListener("click",function(){decCheck();label.innerHTML=value;console.log(value)});inc_btn.addEventListener("click",function(){value++;label.innerHTML=value;console.log(value)});